import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { v4 as uuidv4 } from "uuid";

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = (todo) => {
    setTodos([...todos, { id: uuidv4(), task: todo, status: "todo" }]);
  };

  const moveToInProgress = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, status: "inProgress" } : todo
      )
    );
  };

  const moveToCompleted = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, status: "completed" } : todo
      )
    );
  };

  const updateTask = (id, updatedTask) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, task: updatedTask } : todo
      )
    );
  };

  return (
    <div className="TodoWrapper">
      <h1>Task Manager</h1>
      <TodoForm addTodo={addTodo} />

      <div className="TodoList">
        <h2>To Do</h2>
        {todos
          .filter((todo) => todo.status === "todo")
          .map((todo) => (
            <Todo
              key={todo.id}
              task={todo.task}
              moveToInProgress={() => moveToInProgress(todo.id)}
              moveToCompleted={() => moveToCompleted(todo.id)}
              updateTask={(updatedTask) => updateTask(todo.id, updatedTask)}
            />
          ))}
      </div>

      <div className="InProgressList">
        <h2>In Progress</h2>
        {todos
          .filter((todo) => todo.status === "inProgress")
          .map((todo) => (
            <Todo
              key={todo.id}
              task={todo.task}
              moveToInProgress={() => moveToInProgress(todo.id)}
              moveToCompleted={() => moveToCompleted(todo.id)}
              updateTask={(updatedTask) => updateTask(todo.id, updatedTask)}
            />
          ))}
      </div>

      <div className="CompletedList">
        <h2>Completed</h2>
        {todos
          .filter((todo) => todo.status === "completed")
          .map((todo) => (
            <Todo
              key={todo.id}
              task={todo.task}
              moveToInProgress={() => moveToInProgress(todo.id)}
              moveToCompleted={() => moveToCompleted(todo.id)}
              updateTask={(updatedTask) => updateTask(todo.id, updatedTask)}
            />
          ))}
      </div>
    </div>
  );
};
