import React, { useState } from "react";

export const Todo = ({ task, moveToInProgress, moveToCompleted, updateTask }) => {
  const [editMode, setEditMode] = useState(false);
  const [editedTask, setEditedTask] = useState(task);

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleSave = () => {
    updateTask(editedTask);
    setEditMode(false);
  };

  return (
    <div className="Todo">
      {editMode ? (
        <input
          type="text"
          value={editedTask}
          onChange={(e) => setEditedTask(e.target.value)}
        />
      ) : (
        <p>{task}</p>
      )}
      {editMode ? (
        <button onClick={handleSave}>Save</button>
      ) : (
        <>
          <button onClick={handleEdit}>Edit</button>
          <button onClick={moveToInProgress}>In Progress</button>
          <button onClick={moveToCompleted}>Completed</button>
        </>
      )}
    </div>
  );
};
