import React, { useState } from 'react';

export const Todo = ({ task, moveToInProgress, moveToCompleted, updateTask }) => {
  const [editMode, setEditMode] = useState(false);
  const [editedTask, setEditedTask] = useState(task);

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleSave = () => {
    updateTask(editedTask);
    setEditMode(false);
  };

  return (
    <div className="Todo">
      <div>
        {editMode ? (
          <input
            type="text"
            value={editedTask}
            onChange={(e) => setEditedTask(e.target.value)}
          />
        ) : (
          <p>{task}</p>
        )}
      </div>
      <div>
        {editMode ? (
          <button onClick={handleSave}>Save</button>
        ) : (
          <>
            <button onClick={handleEdit}>Edit</button>
            <button onClick={() => moveToInProgress(task)}>In Progress</button>
            <button onClick={() => moveToCompleted(task)}>Completed</button>
          </>
        )}
      </div>
    </div>
  );
};
