import React, { useState } from 'react';

export const TodoForm = ({ addTodo }) => {
  const [value, setValue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (value) {
      addTodo({
        id: Math.floor(Math.random() * 10000),
        task: value,
        completed: false,
        isEditing: false,
      });
      setValue('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="TodoForm">
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        className="todo-input"
        placeholder="What is the task today?"
      />
      <button type="submit" className="todo-btn">
        Add Task
      </button>
      <button
        type="button"
        className="todo-btn inprogress-btn"
        onClick={() => {
          addTodo({
            id: Math.floor(Math.random() * 10000),
            task: value,
            completed: false,
            isEditing: false,
          });
          setValue('');
        }}
      >
        In Progress
      </button>
      <button
        type="button"
        className="todo-btn completed-btn"
        onClick={() => {
          addTodo({
            id: Math.floor(Math.random() * 10000),
            task: value,
            completed: true,
            isEditing: false,
          });
          setValue('');
        }}
      >
        Completed
      </button>
    </form>
  );
};
